<?php

namespace Richpolis\PublicidadBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * PublicidadRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PublicidadRepository extends EntityRepository
{
     public function getMaxPosicion(){
        $em=$this->getEntityManager();
       
        $query=$em->createQuery('
            SELECT MAX(c.position) as value 
            FROM PublicidadBundle:Publicidad c 
            ORDER BY c.position ASC
        ');
        
        $max=$query->getResult();
        return $max[0]['value'];
    }
    public function getPublicidadActivos(){
        $em=$this->getEntityManager();
        $query=$em->createQuery('
               SELECT e 
               FROM PublicidadBundle:Publicidad e 
               WHERE e.isActive = :active
               ORDER BY e.position ASC
        ')->setParameters(array('active'=>true));
        return $query->getResult();
    }
    
    public function getPublicidadPorTipo($tipoPublicidad){
        $em=$this->getEntityManager();
        $query=$em->createQuery('
               SELECT e 
               FROM PublicidadBundle:Publicidad e 
               WHERE e.tipoPublicidad = :tipo
               ORDER BY e.position ASC
        ')->setParameters(array('tipo'=>$tipoPublicidad));
        return $query->getResult();
    }
    
    public function getPublicidadActual(){
        $em=$this->getEntityManager();
        $query=$em->createQuery('
               SELECT e 
               FROM PublicidadBundle:Publicidad e 
               WHERE e.vigencia >= :fecha
               ORDER BY e.position ASC
        ')->setParameters(array('fecha'=> new \DateTime()));
        return $query->getResult();
    }
}
